import React,{useEffect, useState} from 'react'
import { View } from './Components/View';

//getting data from local storage
const getDatafromLS=()=>{
  const data = localStorage.getItem('receipts');
  if(data){
    return JSON.parse(data);
  }
  else{
    return[]
  }
}
export const App = () => {


  const[receipts, setreceipts]=useState(getDatafromLS());

  const[date, setDate] = useState('');
  const[amount, setAmount] = useState('');
  const[payment_mode, setPayment] = useState('');
  const[remark, setRemark] = useState('');

  const handleSubmit=(e) => {
    e.preventDefault();

    let receipt={
      date:date,
      amount:amount,
      payment_mode:payment_mode,
      remark:remark
    }
    setreceipts([...receipts,receipt]);
    setDate('');
    setAmount('');
    setPayment('');
    setRemark('');
  }

  const handleCancel=(e) => {
    let receipt={
      date,
      amount,
      payment_mode,
      remark
    }
    setreceipts([...receipts,receipt]);
    setDate();
    setAmount();
    setPayment();
    setRemark();
  }

  useEffect(() => {
        localStorage.setItem('receipts', JSON.stringify(receipts));
  },[receipts])

  return (
    <div className='wrapper'>
      <div className='main'>
        <div className='form-container'>
           <form className='form-group' onAbort={handleCancel} onSubmit={handleSubmit} autoComplete='off'>
            <h5>Receipt Details</h5>
            <div>
              <label>Date</label>
              <input type="text" className='form-control' required onChange={(e) => setDate(e.target.value)} value={date}></input>
            </div>
            <div>
              <label>Amount</label>
              <input type="text" className='form-control' required onChange={(e) => setAmount(e.target.value)} value={amount}></input>
            </div>
            <div>
              <label>Payment Mode</label>
              <input type="text" className='form-control' required onChange={(e) => setPayment(e.target.value)} value={payment_mode}></input>
            </div>
            <div>
              <label>Remark</label>
              <input type="text" className='form-control' required onChange={(e) => setRemark(e.target.value)} value={remark}></input>
            </div>
            <div>
             <button type='reset' value="Reset" className='btn btn-default btn-md'>CANCEL <br></br> <u>(ESC)</u></button>
             <button type='submit' className='btn btn-success btn-md'>SUBMIT <br></br> <u>( <i class="bi bi-command"></i>S)</u></button>
             </div>
           </form>
        </div>
        <div className='view-container'>
          {receipts.length >0 && <>
           <div className='table-responsive'>
            <table className='table'>
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Amount</th>
                  <th>Payment Mode</th>
                  <th>Remark</th>
                </tr>
              </thead>
              <tbody>
                <View receipts={receipts}/>
              </tbody>
            </table>
           </div>
          </>}
          {receipts.length < 1 && <div> No Receipts are generated yet </div>}
        </div>
      </div>
    </div>
  )
}

export default App
