import React,{useState} from 'react'

export const App = () => {


  const[receipts, setreceipts]=useState([]);

  const[date, setDate] = useState('');
  const[amount, setAmount] = useState('');
  const[payment_mode, setPayment] = useState('');
  const[remark, setRemark] = useState('');

  const handleSubmit=(e) => {
    e.preventDefault();

    let receipt={
      date:date,
      amount:amount,
      payment_mode:payment_mode,
      remark:remark
    }
    setreceipts([...receipts,receipt]);
    setDate('');
    setAmount('');
    setPayment('');
    setRemark('');
  }

  return (
    <div className='wrapper'>
      <div className='main'>
        <div className='form-container'>
           <form className='form-group' onSubmit={handleSubmit} autoComplete='off'>
            <h5>Receipt Details</h5>
            <div>
              <label>Date</label>
              <input type="text" className='form-control' required onChange={(e) => setDate(e.target.value)} value={date}></input>
            </div>
            <div>
              <label>Amount</label>
              <input type="text" className='form-control' required onChange={(e) => setAmount(e.target.value)} value={amount}></input>
            </div>
            <div>
              <label>Payment Mode</label>
              <input type="text" className='form-control' required onChange={(e) => setPayment(e.target.value)} value={payment_mode}></input>
            </div>
            <div>
              <label>Remark</label>
              <input type="text" className='form-control' required onChange={(e) => setRemark(e.target.value)} value={remark}></input>
            </div>
            <div>
             <button type='submit' className='btn btn-default btn-md'>CANCEL <br></br> <u>(ESC)</u></button>
             <button type='submit' className='btn btn-success btn-md'>SUBMIT <br></br> <u>(BiCommand S)</u></button>
             </div>
           </form>
        </div>
        <div className='view-container'>

        </div>
      </div>
    </div>
  )
}

export default App
